package com.demo.practise;

public class OperatorsDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
			int i=11;//equal to is a assignment
			int j=2;
			
			//Addition
			System.out.println("Addition...:"+(i+j));
			
			//Concatenation
			String s1="hello";
			String s2="world";
			
			System.out.println("Concatenation..:"+s1+s2);
			//only + is overloaded with addition and concatenation and not the other operators
			
			//Subtraction
			
			System.out.println("Substraction..:"+(i-j));
			
			//Multiplication
			System.out.println("Multiplication..:"+(i*j));
			//byte b1=2;byte b2=2;
			//byte result=b1/b2;//the result of arithmetic operation is always int
			
			//division
			System.out.println("Division...quotient.:"+(i/j));
			
			//Mod - To get the reminder from the division operation
			
			System.out.println("Division-Mod-Reminder..:"+(i%2));
			
			

	}

}
